public class DevAxCmmRtsLogUpdateDateDialog extends RunBase
{
    DialogField dateField;
    FormDataSource devAxCmmRtsLogMethods_ds;

}

Method.Source:
public Object dialog()
{
    Dialog dialog = super();

    dateField = dialog.addField(extendedTypeStr(EndDate), "Nueva fecha");

    return dialog;
}

public void run()
{
    if (this.prompt())
    {
        EndDate updateDate = dateField.value();
        if (updateDate)
        {
            DevAxCmmRtsLogMethods devAxCmmRtsLogMethods;
            ttsbegin;
            for(devAxCmmRtsLogMethods = devAxCmmRtsLogMethods_ds.getFirst(true) ? devAxCmmRtsLogMethods_ds.getFirst(true) : devAxCmmRtsLogMethods_ds.cursor(); devAxCmmRtsLogMethods; devAxCmmRtsLogMethods = devAxCmmRtsLogMethods_ds.getNext())
            {
                devAxCmmRtsLogMethods.selectForUpdate(true);

                devAxCmmRtsLogMethods.EndDate = updateDate;

                devAxCmmRtsLogMethods.update();
            }
            ttscommit;

        }
        info("Fechas actualizadas correctamente");
    }
}
